\n\n// 이미지 테스트용 간단한 위젯\n// 메인 화면에 임시로 추가해서 이미지 로드 테스트 가능\nclass ImageTestWidget extends StatelessWidget {\n  const ImageTestWidget({super.key});\n\n  @override\n  Widget build(BuildContext context) {\n    return Container(\n      padding: const EdgeInsets.all(20),\n      color: Colors.black54,\n      child: Column(\n        mainAxisSize: MainAxisSize.min,\n        children: [\n          const Text(\n            '🧪 이미지 테스트',\n            style: TextStyle(color: Colors.white, fontSize: 18, fontWeight: FontWeight.bold),\n          ),\n          const SizedBox(height: 10),\n          \n          // 플레이어 이미지들 테스트\n          Row(\n            mainAxisAlignment: MainAxisAlignment.center,\n            children: [\n              _buildTestImage('assets/images/characters/player/idle.png', 'Player Idle'),\n              const SizedBox(width: 10),\n              _buildTestImage('assets/images/characters/player/attack.png', 'Player Attack'),\n              const SizedBox(width: 10),\n              _buildTestImage('assets/images/characters/player/death.png', 'Player Death'),\n            ],\n          ),\n          \n          const SizedBox(height: 10),\n          \n          // 적 이미지들 테스트\n          Row(\n            mainAxisAlignment: MainAxisAlignment.center,\n            children: [\n              _buildTestImage('assets/images/characters/enemy/idle.png', 'Enemy Idle'),\n              const SizedBox(width: 10),\n              _buildTestImage('assets/images/characters/enemy/attack.png', 'Enemy Attack'),\n              const SizedBox(width: 10),\n              _buildTestImage('assets/images/characters/enemy/death.png', 'Enemy Death'),\n            ],\n          ),\n        ],\n      ),\n    );\n  }\n  \n  Widget _buildTestImage(String path, String label) {\n    return Column(\n      children: [\n        Container(\n          width: 60,\n          height: 60,\n          decoration: BoxDecoration(\n            border: Border.all(color: Colors.white),\n            borderRadius: BorderRadius.circular(8),\n          ),\n          child: Image.asset(\n            path,\n            fit: BoxFit.contain,\n            errorBuilder: (context, error, stackTrace) {\n              print('❌ 테스트 이미지 로드 실패: $path');\n              print('❌ 에러: $error');\n              return const Icon(\n                Icons.error,\n                color: Colors.red,\n                size: 30,\n              );\n            },\n          ),\n        ),\n        const SizedBox(height: 5),\n        Text(\n          label,\n          style: const TextStyle(color: Colors.white, fontSize: 10),\n        ),\n      ],\n    );\n  }\n}\n